      $db_host = $row["DB_HOST"];
      $db_name = $row["DB_SCHEMA"];
      $db_charset = $row["DB_CHARSET"];
      $db_user = $row["DB_USER"];
      $db_pass = $row["DB_PASS"];
      $db_table = $row["TBL_ROUTES"]; 
    } catch (PDOException $e) {
      $error_message = "{\"error\": \"internal error {$e}\"}";
